// src/App.js
import React, { useState } from "react";
import WorkoutForm from "./components/WorkoutForm";
import WorkoutList from "./components/WorkoutList";
import WorkoutChart from "./components/WorkoutChart";

function App() {
  const [workouts, setWorkouts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState("All");
  const [currentPage, setCurrentPage] = useState(1);
  const workoutsPerPage = 5;

  // Add new workout to the list
  const handleAddWorkout = (newWorkout) => {
    setWorkouts([...workouts, newWorkout]);
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
    setCurrentPage(1); // Reset to first page on search change
  };

  // Handle workout type filter change
  const handleFilterChange = (e) => {
    setFilterType(e.target.value);
    setCurrentPage(1); // Reset to first page on filter change
  };

  // Paginate the workouts list
  const indexOfLastWorkout = currentPage * workoutsPerPage;
  const indexOfFirstWorkout = indexOfLastWorkout - workoutsPerPage;
  const currentWorkouts = workouts
    .filter(
      (workout) =>
        (filterType === "All" || workout.workoutType === filterType) &&
        workout.name.toLowerCase().includes(searchQuery.toLowerCase())
    )
    .slice(indexOfFirstWorkout, indexOfLastWorkout);

  // Handle pagination
  const totalPages = Math.ceil(workouts.length / workoutsPerPage);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="App">
      <h1>Workout Tracker</h1>
      <WorkoutForm onAddWorkout={handleAddWorkout} />

      {/* Search and Filter Section */}
      <div className="search-filter-container">
        <input
          type="text"
          placeholder="Search by name"
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <select value={filterType} onChange={handleFilterChange}>
          <option value="All">All Workout Types</option>
          <option value="Cardio">Cardio</option>
          <option value="Strength">Strength</option>
          <option value="Yoga">Yoga</option>
        </select>
      </div>

      {/* Workout List */}
      <WorkoutList workouts={currentWorkouts} />

      {/* Pagination */}
      <div className="pagination">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>

      {/* Workout Progress Chart */}
      <WorkoutChart workouts={workouts} />
    </div>
  );
}

export default App;
