import React, { useState } from "react";

const App = () => {
  const [notes, setNotes] = useState([]);
  const [text, setText] = useState("");
  const [isListening, setIsListening] = useState(false);

  const handleAddNote = () => {
    if (text.trim()) {
      setNotes([...notes, { type: "text", content: text }]);
      setText("");
    }
  };

  const handleAddURL = () => {
    if (text.trim() && text.startsWith("http")) {
      setNotes([...notes, { type: "url", content: text }]);
      setText("");
    }
  };

  const handleSpeechRecognition = () => {
    const recognition = new window.SpeechRecognition();
    recognition.continuous = false;
    recognition.interimResults = false;
    recognition.lang = "en-US";

    recognition.onstart = () => setIsListening(true);
    recognition.onend = () => setIsListening(false);

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setNotes([...notes, { type: "audio", content: transcript }]);
    };

    recognition.start();
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Note-Taking App</h2>
      <textarea
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Enter text or URL..."
        rows={3}
      />
      <div>
        <button onClick={handleAddNote}>Add Note</button>
        <button onClick={handleAddURL}>Add URL</button>
        <button onClick={handleSpeechRecognition} disabled={isListening}>
          {isListening ? "Listening..." : "Start Speech"}
        </button>
      </div>
      <ul>
        {notes.map((note, index) => (
          <li key={index}>
            {note.type === "url" ? (
              <a href={note.content} target="_blank" rel="noopener noreferrer">
                {note.content}
              </a>
            ) : (
              note.content
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
